import { createSlice } from "@reduxjs/toolkit";

const signUp = createSlice({
    name: 'users',
    initialState: [],
    reducers: {
        setUsers(state, action) {
            return action.payload
        }, 
        appendUser(state, action) {
            state.push(action.payload);
        }
    }
})
