import { useEffect, useState } from "react";

const Comment = ({ comment }) => {
    return (
        <div>
            {comment.body}
        </div>
    )
};

const CommentList = ({ blog }) => {
    const [ comments, setComments ] = useState([])

    useEffect(() => {
        if (blog.comments) {
            setComments(blog.comments)
        }
    }, []);

    return (
        <div>
            <h3>Comments</h3>
            {comments.map(comment => 
                <Comment 
                    comment={comment}
                />
                )}
        </div>
    )
};

export default CommentList;
