import { useEffect, useState } from "react";

const Comment = ({ comment }) => {
    return (
        <div>
            {comment.body}
        </div>
    )
};

const CommentList = ({ blog }) => {
    const [ comments, setComments ] = useState([])

    useEffect(() => {
        setComments(blog.comments)
    });

    return (
        <div>
            {comments.map(comment => 
                <Comment 
                    comment={comment}
                />
                )}
        </div>
    )
};

export default CommentList;
