import { useDispatch, useSelector } from "react-redux";
import {
    BrowserRouter as Router,
    Routes, Route, Link
  } from 'react-router-dom';

function countBlogs(array) {
    let count = 0;
    for (let i = 0; i < array.length; i++) {
        count++;
    };
    return count;
};

const User = ({ user }) => {
    let blogCount = countBlogs(user.blogs);

    return (
            <tr>
                    <td >
                    <Link to={`/${user.name}`} >{user.name} - </Link>
                    </td>
                    <td >{blogCount}</td>
            </tr>
    );
};

const UserList = () => {
    const users = useSelector((state) => state.users);
    return (
        <div>
            <h2>Users</h2>
            <table >
                <thead>
                    <tr>
                        <td></td>
                        <td>Blogs created</td>
                    </tr>
                </thead>
                <tbody>
                <Router >
                    {users.map(user => 
                        <User 
                            key={user.id}
                            user={user}
                        />
                    )}
                </Router>
                </tbody>
            </table>
        </div>
    );
};

export default UserList;
